css:
body{
  margin:0;
  padding:0;
  font-family:Arial, Helvetica, sans-serif;
  font-size:11px;
  color:#000;
}

/* ===== A4 PORTRAIT PAGE ===== */
.page{
  width:794px;
  min-height:1123px;
  margin:auto;
  padding:30px;
  box-sizing:border-box;
  background:#fff;
  page-break-after:always;
}

/* ===== LOGO ===== */
.logo-div{
  width:100%;
  text-align:left;
  margin-bottom:20px;
}

.logo-div img{
  width:110px;
  height:auto;
}

/* ===== PAGE 1 FULL IMAGE ===== */
.page1{
  padding:0;
}

.page1 img{
  width:100%;
  height:1123px;
  object-fit:cover;
}

/* Container for the specific page */
.page-full-img {
    width: 210mm;
    height: 297mm;
    position: relative;
    overflow: hidden;
    page-break-after: always;
    background: #fff;
    margin: 0 auto;
}

/* The wrapper that spins the image */
.rotate-wrapper {
    position: absolute;
    top: 50%;
    left: 50%;
    
    /* We swap the dimensions: 
       The width of this box = Portrait Height (297mm)
       The height of this box = Portrait Width (210mm) */
    width: 297mm;
    height: 210mm;
    
    /* Center it and rotate */
    transform: translate(-50%, -50%) rotate(90deg);
    
    display: flex;
    justify-content: center;
    align-items: center;
}

.rotate-wrapper img {
    width: 100%;
    height: 100%;
    object-fit: contain; /* Use 'cover' if you want zero white margins */
    display: block;
}

/* Ensure no overlap in ERPNext/wkhtmltopdf */
@media print {
    .page-full-img {
        display: block;
        float: none;
        page-break-inside: avoid;
    }
}

/* ===== PAGE 3 ===== */
.page3-header {
  display:flex;
  justify-content:space-between;
  align-items:center;
  font-size:10px;
}

.page3-header img {
  height:40px;
}

.page3-title {
  text-align:center;
  font-weight:bold;
  margin:10px 0 15px;
  text-decoration:underline;
}

.page3-subtitle {
  font-weight:bold;
  margin-bottom:10px;
}

/* Layout */
.page3-content {
  display:flex;
  gap:30px;
}

.page3-left {
  width:75%;
}

.page3-right {
  width:25%;
  font-size:10px;
  margin-left:20px;
}

/* Section Header */
.section-title {
  background:#c9d9eb;
  padding:4px 6px;
  font-weight:bold;
  margin:10px 0 5px;
}

/* Tables */
.report-table {
  width:100%;
  border-collapse:collapse;
  font-size:10px;
}

.report-table th,
.report-table td {
  border:1px solid #000;
  padding:3px 5px;
}

.report-table th {
  background:#e6eef7;
  text-align:left;
}

.highlight {
  background:#ffd966;
  font-weight:bold;
  text-align:center;
}

/* Quarter table */
.center {
  text-align:center;
}

/* Acknowledgement */
.ack-box {
  margin-top:60px;
}

.ack-box p {
  margin:25px 0 5px;
}

/* ===== TABLES ===== */
table{
  width:100%;
  border-collapse:collapse;
}

table td{
  border:1px solid #000;
  padding:6px;
}

table tr.bold td{
  font-weight:bold;
}

table.financial td{
  font-size:10px;
  padding:4px;
}

/* ===== ACKNOWLEDGEMENT ===== */
.acknowledgement{
  display:flex;
  justify-content:space-between;
  margin-top:30px;
}

/* ===== PRINT ===== */
@media print{
  @page{
    size:A4 portrait;
    margin:0;
  }
}
.gain-section-title {
        font-size: 16px;
        font-weight: bold;
        text-decoration: underline;
        margin-bottom: 10px;
        font-family: sans-serif;
    }
    .report-table {
        width: 100%;
        border-collapse: collapse;
        font-family: sans-serif;
        margin-bottom: 20px;
    }
    .report-table th {
        background-color: #000;
        color: #fff;
        border: 1px solid #000;
        padding: 8px;
        text-align: left;
    }
    .report-table td {
        border: 1px solid #000;
        padding: 8px;
        vertical-align: middle;
    }
    .text-center {
        text-align: center;
    }
    .font-bold-italic {
        font-weight: bold;
        font-style: italic;
    }
