css:
* {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            font-family: Arial, Helvetica, sans-serif;
        }

        body {
            background: #f5f5f5;
            color: #333;
            line-height: 1.45;
            font-size: 10.5pt;
        }

        .container {
            max-width: 1000px;
            margin: 0 auto;
            background: white;
            padding: 25px;
        }

        .company-header {
            text-align: center;
            margin-bottom: 20px;
        }

        .company-header h1 {
            font-size: 18pt;
            color: #2c3e50;
            margin-bottom: 5px;
        }

        .company-info {
            font-size: 10pt;
            margin-bottom: 15px;
        }

        .invoice-section {
            margin-bottom: 60px;
            page-break-after: always;
        }

        .invoice-meta {
            display: flex;
            justify-content: space-between;
            margin-bottom: 15px;
            font-size: 10pt;
        }

        .party-table {
            width: 100%;
            border-collapse: collapse;
            margin-bottom: 20px;
        }

        .party-table th, .party-table td {
            padding: 4px 6px;
            border: 1px solid #ccc;
            vertical-align: top;
        }

        .party-table th {
            background: #f0f0f0;
            text-align: left;
            width: 18%;
        }

        .items-table {
            width: 100%;
            border-collapse: collapse;
            margin: 15px 0 20px;
            font-size: 9.5pt;
        }

        .items-table th {
            background: #4CAF50;
            color: white;
            padding: 8px 6px;
            text-align: left;
        }

        .items-table td {
            padding: 6px;
            border: 1px solid #ccc;
        }

        .totals {
            text-align: right;
            font-weight: bold;
            margin-top: 15px;
        }

        .signature-area {
            display: flex;
            justify-content: space-between;
            margin-top: 50px;
        }

        .sig-box {
            width: 45%;
            text-align: center;
        }

        .sig-line {
            border-top: 1px solid #333;
            margin: 45px 0 8px;
        }

        .footer {
            text-align: center;
            font-size: 9pt;
            color: #666;
            margin-top: 40px;
            border-top: 1px solid #eee;
            padding-top: 12px;
        }

        @media print {
            body { background: white; margin: 0; padding: 0; }
            .container { box-shadow: none; padding: 15px 20px; }
            .no-print { display: none !important; }
            .invoice-section { page-break-after: always; }

html:
{% set company = frappe.get_doc("Company", doc.company) %}
{% set supplier_address = frappe.get_doc("Address", company.custom_registered_address) if company.custom_registered_address else "" %}

<div class="container">
    <div class="company-header">
        <h1>SALES TAX INVOICE</h1>
        <div class="company-info">
            {{ company.company_name or "SIDDIQ RENEWABLE ENERGY (PRIVATE) LIMITED" }}<br>
            {% if supplier_address %}
                {{ supplier_address.address_line1 or "" }}<br>
                {% if supplier_address.address_line2 %}{{ supplier_address.address_line2 }}<br>{% endif %}
                {{ supplier_address.city or "" }}, {{ supplier_address.state or "" }} - {{ supplier_address.country or "Pakistan" }}<br>
            {% else %}
                47 - G.T. ROAD, HIDE MARKET – LAHORE<br>
            {% endif %}
            ☎ {{ company.phone_no or "+92-42-37970576-78" }}   
            {% if company.fax %}📠 {{ company.fax }} {% endif %}
            ✉ {{ company.email or "info@sre.com.pk" }}
        </div>
    </div>

    <div class="invoice-section">
        <div class="invoice-meta">
            <div>
                <strong>Date:</strong> {{ frappe.utils.format_date(doc.posting_date, "dd MMM yyyy") }}<br>
                <strong>Comm. Invoice No.:</strong>{{ doc.name }}
            </div>
            <div style="text-align: right;">
                <strong>Sales Tax Invoice No.:</strong>{{ doc.name }}
            </div>
        </div>

        <table class="party-table">
            <tr>
                <th>Supplier's Name:</th>
                <td>{{ company.company_name or "" }}</td>
                <th>NTN No.:</th>
                <td>{{ company.tax_id or "" }}</td>
                <th>Sales Tax Reg No.:</th>
                <td>{{ company.custom_strn or company.custom_sales_tax_reg_no or "" }}</td>
            </tr>
            <tr>
                <th>Supplier Address:</th>
                <td colspan="5">
                    {% if supplier_address %}
                        {{ supplier_address.address_line1 or "" }}
                        {% if supplier_address.address_line2 %}, {{ supplier_address.address_line2 }}{% endif %}
                        {% if supplier_address.city %}, {{ supplier_address.city }}{% endif %}
                        {% if supplier_address.state %}, {{ supplier_address.state }}{% endif %}
                        {% if supplier_address.pincode %} - {{ supplier_address.pincode }}{% endif %}
                        {% if supplier_address.country %}, {{ supplier_address.country }}{% endif %}
                    {% else %}
                        {{ company.custom_registered_address_display or "" }}
                    {% endif %}
                </td>
            </tr>
            <tr>
                <th>Telephone No.:</th>
                <td colspan="5">{{ company.phone_no or "" }}</td>
            </tr>
            <tr>
                <th>Buyer's Name:</th>
                <td>{{ doc.customer_name or doc.customer or "" }}</td>
                <th>NTN/CNIC No.:</th>
                <td>
                    {% if doc.tax_id %}
                        {{ doc.tax_id }}
                    {% elif doc.custom_cnic %}
                        {{ doc.custom_cnic }}
                    {% else %}
                        {{ buyer_cnic_ntn or "" }}
                    {% endif %}
                </td>
                <th>Sales Tax Reg No.:</th>
                <td>{{ doc.tax_strn or doc.custom_strn or buyer_strn or "" }}</td>
            </tr>
            <tr>
                <th>Buyer's Address:</th>
                <td colspan="5">
                    {% if doc.customer_address %}
                        {{ doc.customer_address }}
                    {% elif doc.address_display %}
                        {{ doc.address_display }}
                    {% elif doc.shipping_address_name %}
                        {% set shipping_address = frappe.get_doc("Address", doc.shipping_address_name) %}
                        {{ shipping_address.address_line1 or "" }}
                        {% if shipping_address.address_line2 %}, {{ shipping_address.address_line2 }}{% endif %}
                        {% if shipping_address.city %}, {{ shipping_address.city }}{% endif %}
                        {% if shipping_address.state %}, {{ shipping_address.state }}{% endif %}
                        {% if shipping_address.pincode %} - {{ shipping_address.pincode }}{% endif %}
                    {% else %}
                        {{ buyer_address or "" }}
                    {% endif %}
                </td>
            </tr>
            <tr>
                <th>Telephone No.:</th>
                <td colspan="5">{{ doc.contact_mobile or doc.contact_phone or doc.custom_phone or buyer_phone or "" }}</td>
            </tr>
        </table>

        {% set ns = namespace(
            sales_tax=0.0,
            sales_rate=None
        ) %}

        {% for tax in doc.taxes %}
            {% set desc = (tax.description or "") | lower %}
            {% set ah = (tax.account_head or "") | lower %}
            {% set rate = tax.rate | float if tax.rate else 0.0 %}
            {% set amount = tax.tax_amount | float if tax.tax_amount else 0.0 %}

            {% if ('sales tax' in desc or 'gst' in desc or 'sales tax' in ah)
                and 'advance' not in desc
                and 'withholding' not in desc
                and 'further' not in desc
            %}
                {% set ns.sales_tax = ns.sales_tax + amount %}
                {% if ns.sales_rate is none %}
                    {% set ns.sales_rate = rate %}
                {% endif %}
            {% endif %}
        {% endfor %}

        {% set global_sales_tax_rate = ns.sales_rate or 0 %}

        <table class="items-table">
            <thead>
                <tr>
                    <th>Quantity</th>
                    <th>Units</th>
                    <th>Description</th>
                    <th>Unit Price</th>
                    <th>Total Value (Ex. GST)</th>
                    <th>Sales Tax Rate</th>
                    <th>Sales Tax Amount</th>
                    <th>Total Value (Incl. GST)</th>
                </tr>
            </thead>
            <tbody>
                {% set totals = namespace(ex_gst=0, tax=0, incl_gst=0) %}

                {% for item in doc.items %}
                    {% set item_qty = item.qty or 0 %}
                    {% set item_rate = item.rate or 0 %}
                    {% set item_net_amount = item_qty * item_rate %}

                    {% if item.amount %}
                        {% set item_net_amount = item.amount %}
                    {% endif %}

                    {% set item_tax_rate = item.tax_rate if item.tax_rate else global_sales_tax_rate %}
                    {% set item_tax_amount = item_net_amount * (item_tax_rate / 100) %}
                    {% set item_ex_gst = item_net_amount %}
                    {% set item_incl_gst = item_net_amount + item_tax_amount %}

                    {% set totals.ex_gst = totals.ex_gst + item_ex_gst %}
                    {% set totals.tax = totals.tax + item_tax_amount %}
                    {% set totals.incl_gst = totals.incl_gst + item_incl_gst %}

                    <tr>
                        <td>{{ item_qty }}</td>
                        <td>{{ item.uom or "Nos" }}</td>
                        <td>{{ item.item_name or item.description or item.item_code or "" }}</td>
                        <td>{{ frappe.format(item_rate, { fieldtype: "Currency" }) }}</td>
                        <td>{{ frappe.format(item_ex_gst, { fieldtype: "Currency" }) }}</td>
                        <td>
                            {% if item_tax_rate == 0 %}
                                Exempt
                            {% else %}
                                {{ item_tax_rate }}%
                            {% endif %}
                        </td>
                        <td>{{ frappe.format(item_tax_amount, { fieldtype: "Currency" }) }}</td>
                        <td>{{ frappe.format(item_incl_gst, { fieldtype: "Currency" }) }}</td>
                    </tr>
                {% endfor %}

                <tr style="font-weight: bold;">
                    <td colspan="4"><strong>Total</strong></td>
                    <td>{{ frappe.format(totals.ex_gst, { fieldtype: "Currency" }) }}</td>
                    <td></td>
                    <td>{{ frappe.format(totals.tax, { fieldtype: "Currency" }) }}</td>
                    <td>{{ frappe.format(totals.incl_gst, { fieldtype: "Currency" }) }}</td>
                </tr>
            </tbody>
        </table>

        <div class="totals">
            <strong>Sales Tax:</strong>{{ frappe.format(totals.tax, { fieldtype: "Currency" }) }}<br>
            <strong>Net. Tax Inclusive Value:</strong>{{ frappe.format(totals.incl_gst, { fieldtype: "Currency" }) }}
        </div>

        <div class="signature-area">
            <div class="sig-box">
                <div class="sig-line"></div>
                <p>Signature</p>
            </div>
            <div class="sig-box">
                <div class="sig-line"></div>
                <p>Name & Designation</p>
            </div>
        </div>
        <div class="footer">
        A Siddiq Shafi Group Company
    </div>
    </div>


</div>
